import React, { useState } from 'react';
import './TaskList.css';

function TaskList({ task, handleDelete}) {
  const [isClicked, setIsClicked] = useState(false);

  function handleClick() {
    setIsClicked(!isClicked);
  }

  function handleDeleteButton() {
    handleDelete(task.title);
  }
  return (
    <>
      <div className='d-flex'>
        <div className={`task-container ${isClicked ? 'clicked' : ''}`} onClick={handleClick}>
          <div className='fs-5'><b>{task.title}</b></div>
          <div>{task.description}</div>
        </div>
         {isClicked && (<button className="btn btn-danger hidden-button ms-3" onClick={() => handleDelete(task.title)}>
            Delete
          </button>
        )}
      </div>
    </>
  );
}

export default TaskList;


function handleInputChange(e) {
  setValue(e.target.value);
  setIsTitleValid(true);  
}
