import React, { useState } from 'react';
import './TaskList.css';

function TaskList({ task, handleDelete}) {
  const [isClicked, setIsClicked] = useState(false);

  function handleClick() {
    setIsClicked(!isClicked);
    handleDelete(task.title);
  }

  // function handleButtonClick() {
  //   const result = window.confirm('Are you sure you want to delete?');
  //   if (result) {
  //     console.log('OK button clicked');
  //   } else {
  //     console.log('Cancel button clicked');
  //   }
  // };
  // function handleDeleteButton() {
  //   handleButtonClick();
  //   handleDelete(task.title);
  //   setIsClicked(false);
  // }
  return (
    <>
      <div className='d-flex'>
        <div className={`task-container ${isClicked ? 'clicked' : ''}`} onClick={handleClick}>
          <div className='fs-5'><b>{task.title}</b></div>
          <div>{task.description}</div>
        </div>
         {/* {isClicked && (<button className="btn btn-danger hidden-button ms-3" onClick={handleDeleteButton}>
            Delete
          </button>
        )} */}
      </div>
    </>
  );
}

export default TaskList;
