import React,{useState} from "react";
import AddTodo from "../AddTodo/AddTodo";
import "./Home.css";
import TaskList from "../TaskList/TaskList";
function Home() {
  const [show, setShow] = useState(false);
  const [task, setTask] = useState([
    // {title:'learn html', description: 'hjjkjkjkjk'},
    // {title:'learn css', description: 'hjjkjkjkjk'},
    // {title:'learn jsx', description: 'hjjkjkjkjk'},
    // {title:'learn html', description: 'hjjkjkjkjk'},
    // {title:'learn react', description: 'hjjkjkjkjk'},
  ]);
  const addTask = (title, description) => {
    const newTask = [...task, { title, description }];
    setTask(newTask);
  }

  const [data, setData] = useState("");

  // useEffect to retrieve data from localStorage on component mount
  useEffect(() => {
    const storedData = localStorage.getItem('myData');
    if (storedData) {
      setData(storedData);
    }
  }, []);

  // Function to handle data input and store it in localStorage
  const handleDataInput = (event) => {
    const newData = event.target.value;
    setData(newData);
    localStorage.setItem('myData', newData);
  };


  return (
      <>
      <div className="container">
        <input type="text" className="search mt-5" placeholder="search . . ."/>
        <button type="button" className="btn btn-warning ms-3 mb-2">Search</button>
        <button type="button" className="btn btn-primary ms-3 mb-2" onClick={() => setShow(true)}>Add</button>
        <div>
        {task.map((task) =>
        <TaskList task = {task}/>
        )}
        </div>
        <AddTodo show={show} setShow={setShow} addTask={addTask}/>
      </div>
    </>
  );
}

export default Home;
