import React, { useState } from 'react';
import './TaskList.css';

function TaskList({ task, handleDelete, selectAll}) {
  const [isClicked, setIsClicked] = useState(false);

  function handleClick() {
    setIsClicked(!isClicked);
  }

  function handleDeleteButton() {
    alert("Are you sure want to delete?");
    handleDelete(task.title);
    setIsClicked(false);
  }
  return (
    <>
      <div className='d-flex'>
        <div className={`task-container ${isClicked ? 'clicked' : ''}`} onClick={handleClick}>
          <div className='fs-5'><b>{task.title}</b></div>
          <div>{task.description}</div>
        </div>
         {isClicked && (<button className="btn btn-danger hidden-button ms-3" onClick={handleDeleteButton}>
            Delete
          </button>
        )}
      </div>
    </>
  );
}

export default TaskList;
