import React,{useState, } from "react";
import AddTodo from "../AddTodo/AddTodo";
import "./Home.css";
import TaskList from "../TaskList/TaskList";
function Home() {
  const [show, setShow] = useState(false);
  const [task, setTask] = useState([

  ]);
  useEffect(() => {
    const storedTasks = localStorage.getItem('tasks');
    if (storedTasks) {
      setTask(JSON.parse(storedTasks));
    }
  }, []);

  // Update local storage whenever tasks change
  useEffect(() => {
    localStorage.setItem('tasks', JSON.stringify(task));
  }, [task]);


  const addTask = (title, description) => {
    const newTask = [...task, { title, description }];
    setTask(newTask);
  }

  

  return (
      <>
      <div className="container">
        <input type="text" className="search mt-5" placeholder="search . . ."/>
        <button type="button" className="btn btn-warning ms-3 mb-2">Search</button>
        <button type="button" className="btn btn-primary ms-3 mb-2" onClick={() => setShow(true)}>Add</button>
        <div>
        {task.map((task) =>
        <TaskList task = {task}/>
        )}
        </div>
        <AddTodo show={show} setShow={setShow} addTask={addTask}/>
      </div>
    </>
  );
}

export default Home;
