import React,{useState, useEffect} from "react";
import AddTodo from "../AddTodo/AddTodo";
import "./Home.css";
import TaskList from "../TaskList/TaskList";
import {InputGroup, Button, Form} from 'react-bootstrap';
import NotFound from "../NotFound/NotFound";
function Home() {
  
  /*to show/hides the modal */
  const [show, setShow] = useState(false);

  /*original/update task state */
  const [task, setTask] = useState([]);

  /*to perform search */
  const [search, setSearch] = useState('');
  
  const [searchList, setsearchList] = useState([]);
  
  /*to set the todos from local storage to state*/
  useEffect(() => {
    const storedTodos = localStorage.getItem("tasks");
    if (storedTodos) {
      setTask(() => {
        // Using functional update to avoid the 'task' dependency
        const parsedTodos = JSON.parse(storedTodos);
        setsearchList([...parsedTodos]);
        return parsedTodos;
      });
    }
  }, []);

  const handleInputChange = (e) => {
    setSearch(e.target.value);
    if(e.target.value === '') {
      setsearchList([...task]);
    }
  };

  function handleDelete(title) {
    const updatedTasks = task.filter((t) => t.title !== title);
    setTask(updatedTasks);
    setsearchList(updatedTasks);
    localStorage.setItem("tasks", JSON.stringify(updatedTasks));
  }


  function addTask (title, description)  {
    const newTask = [...task, { title, description }];
    setTask(newTask);
  }

  function searchItem () {
    const list = task.filter(item => item.title === search);
    setsearchList([...list]);
  }
  function clear () {
    setSearch('');
    setsearchList([...task]);
  }

  return (
      <>
      <div className="container">
        <h1 className="pt-2">My Todo List</h1>
        <div className="d-flex mt-3">
        <InputGroup className="boot-search">
        <Form.Control placeholder="Search . . ." aria-label="Search" aria-describedby="basic-addon2" value={search} onChange={handleInputChange}/>
        <Button variant="outline-secondary" id="button-addon2" onClick={clear}>X</Button></InputGroup>
        <button type="button" className="btn btn-warning ms-3 mb-2" onClick={searchItem}>Search</button>
        <button type="button" className="btn btn-primary ms-3 mb-2" onClick={() => setShow(true)}>Add</button>
        </div>
        <div>     
        {searchList.length > 0 
        ? (searchList?.map((task, index) => <TaskList key={index} task = {task} />))
        : <NotFound/>
       }
        </div>
        <AddTodo show={show} setShow={setShow} addTask={addTask}/>
      </div>
    </>
  );
}

export default Home;
