import React, { useState, useEffect } from "react";
import AddTodo from "../AddTodo/AddTodo";
import "./Home.css";
import TaskList from "../TaskList/TaskList";
function Home() {
  const [show, setShow] = useState(false);
  const [task, setTask] = useState([]);
  const [searchList, setSearchList] = useState([]);
  const [search, setSearch] = useState("");
  const addTask = (title, description) => {
    const newTask = [...task, { title, description }];
    setTask(newTask);
  };

  useEffect(() => {
    const storedTodos = localStorage.getItem("tasks");
    console.log(storedTodos);
    if (storedTodos) {
      setTask(JSON.parse(storedTodos));
    }
  }, []);

  const searchItem = () => {
    const list = task.filter((item) => item.title === search);
    console.log(search);
    setSearchList(list);
  };

  return (
    <>
      <div className="container">
        <input
          type="text"
          className="search mt-5"
          placeholder="search . . ."
          onChange={(e) => setSearch(e.target.value)}
        />
        <button
          type="button"
          className="btn btn-warning ms-3 mb-2"
          onClick={searchItem}
        >
          Search
        </button>
        <button
          type="button"
          className="btn btn-primary ms-3 mb-2"
          onClick={() => setShow(true)}
        >
          Add
        </button>
        <div>
          {searchList.length > 0
            ? searchList.map((task, index) => (
                <TaskList task={task} key={index} />
              ))
            : task.map((task, index) => <TaskList task={task} key={index} />)}
        </div>
        <AddTodo show={show} setShow={setShow} addTask={addTask} />
      </div>
    </>
  );
}

export default Home;
