import React, { useState, useEffect } from 'react'
import {Modal, Button} from 'react-bootstrap';
import './AddTodo.css'
function AddTodo(props) {

 function handleClose() {
    props.setShow(false); 
  } 
  const [value, setValue] = useState("");
  const [des, setDes] = useState("");

  
  // const addItem = () => {
  //   props.addTask(value, des);
  //   setValue("");
  //   setDes("");
  //   props.setShow(false); 
  // }

  useEffect(() => {
    // Load tasks from local storage when the component mounts
    const existingTasks = JSON.parse(localStorage.getItem('tasks')) || [];
    // Implement logic to update the component state with existingTasks if needed
  }, []); // Empty dependency array to run the effect only once when the component mounts



  const addItem = () => {
    props.addTask(value, des);

    const existingTasks = JSON.parse(localStorage.getItem('tasks')) || [];
    const updatedTasks = [...existingTasks, { value, des }];
    localStorage.setItem('tasks', JSON.stringify(updatedTasks));

    setValue("");
    setDes("");
    props.setShow(false);
  };
  return (
    <div>
     <Modal show={props.show} onHide={handleClose} className='modal-container'>
    <Modal.Header closeButton>
      <Modal.Title>New Task</Modal.Title>
      <input type="text" className='ms-2 p-2 dask-title' value={value} onChange={(e) => setValue(e.target.value)}/>
    </Modal.Header>
      <textarea name="textarea" id="textarea" value={des} placeholder='Add task description....' className='area ms-1' onChange={(e) => setDes(e.target.value)}/>
    <Modal.Footer>
      <Button variant="secondary" onClick={handleClose}>
        Close
      </Button>
      <Button variant="primary" onClick={addItem} >Save</Button>
    </Modal.Footer>
  </Modal></div>
  )
}

export default AddTodo
