import React, { useState } from 'react';
import { Modal, Button } from 'react-bootstrap';
import './AddTodo.css';

function AddTodo(props) {
  const [value, setValue] = useState("");
  const [des, setDes] = useState("");

  const handleClose = () => {
    props.setShow(false);
  };

  const addItem = () => {
    props.addTask(value, des);

    // Retrieve existing tasks from local storage (if any)
    const existingTasks = JSON.parse(localStorage.getItem('tasks')) || [];

    // Add the new task details to the existing tasks array
    const updatedTasks = [...existingTasks, { value, des }];

    // Store the updated tasks array back in local storage
    localStorage.setItem('tasks', JSON.stringify(updatedTasks));

    setValue("");
    setDes("");
    props.setShow(false);
  };

  return (
    <div>
      <Modal show={props.show} onHide={handleClose} className='modal-container'>
        <Modal.Header closeButton>
          <Modal.Title>New Task</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <input
            type="text"
            className='ms-2 p-2 dask-title'
            value={value}
            onChange={(e) => setValue(e.target.value)}
            placeholder="Task Title"
          />
          <textarea
            name="textarea"
            id="textarea"
            value={des}
            placeholder='Add task description....'
            className='area ms-1'
            onChange={(e) => setDes(e.target.value)}
          />
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={addItem}>
            Save
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default AddTodo;
