        <input type="text" className="search mt-5" placeholder="search . . ." onChange={(e) => setSearch(e.target.value)}/>
        <button type="button" className="btn btn-warning ms-3 mb-2" onClick={() => setSearch(true)}>Search</button>
        <button type="button" className="btn btn-primary ms-3 mb-2" onClick={() => setShow(true)}>Add</button>
        <div>
        {task.map((task, index) =>
        <TaskList task = {task} key={index}/>
