import React, { useState } from 'react'
import {Modal, Button} from 'react-bootstrap';
import './AddTodo.css'
function AddTodo(props) {

 function handleClose() {
    props.setShow(false); 
  } 
  const [title, setValue] = useState("");
  const [description, setDes] = useState("");
  const [isTitleValid, setIsTitleValid] = useState(true);

  const addItem = () => {
    if (title.trim() === '') {
      // If title is empty, set validity to false and return
      setIsTitleValid(false);
      return;
    }

    props.addTask(title, description);
    const existingTasks = JSON.parse(localStorage.getItem('tasks')) || [];
    const updatedTasks = [...existingTasks, { title, description }];
    localStorage.setItem('tasks', JSON.stringify(updatedTasks));

    setValue("");
    setDes("");
    props.setShow(false);
  };
  const checkItem = () => {
     
  }

  return (
    <div>
     <Modal show={props.show} onHide={handleClose} className='modal-container'>
    <Modal.Header closeButton>
      <Modal.Title>New Task</Modal.Title>
      <input type="text" placeholder='title' className={`ms-2 p-2 dask-title ${!isTitleValid ? 'is-invalid' : ''}`} value={title} onChange={(e) => setValue(e.target.value)} required/>
    </Modal.Header>
      <textarea name="textarea" id="textarea" value={description} placeholder='add task description....' className='area ms-1 ps-2' onChange={(e) => setDes(e.target.value)}/>
    <Modal.Footer>
      <Button variant="secondary" onClick={handleClose}>Cancel</Button>
      <Button variant="primary" onClick={addItem} >Save</Button>
    </Modal.Footer>
  </Modal></div>
  )
}

export default AddTodo
